mapper.update:
UPDATE 
	trip t
SET 


		trip_name = #{tripName}


		,trip_destination = #{tripDestination}


		,trip_champion = #{tripChampion}


		,has_trip_started = #{hasTripStarted}


		,start_time = #{startTime}


		,end_time = #{endTime}


		,created_at = #{createdAt}


		,updated_at = #{updatedAt}

WHERE
	t.id = #{id}
